#include <metal_stdlib>
#include <simd/simd.h>

using namespace metal;

struct red-original_fragment_main_out
{
    float4 fragColor [[color(0)]];
};

fragment red-original_fragment_main_out red-original_fragment_main(constant float& iTime [[buffer(0)]])
{
    red-original_fragment_main_out out = {};
    out.fragColor = float4(1.0, cos(iTime), 0.0, 1.0);
    return out;
}
